import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-prod-table-list',
  templateUrl: './prod-table-list.component.html',
  styleUrls: ['./prod-table-list.component.css']
})
export class ProdTableListComponent implements OnInit{

  public theaters: TheaterModel[] = [];
  public movies: MovieModel[] = [];

  public constructor(private prodService: DataService) { }

  public async ngOnInit() {
      try {
          this.theaters = await this.dataService.getAllTheaters();
      }
      catch (err: any) {
          alert(err.message);
      }
  }

}
