import { Component, OnInit } from '@angular/core';
import CatrgoryModel from 'src/app/models/CategoryModel';
import ProductModel from 'src/app/models/ProductModel';
import { ProductsServiceService } from 'src/app/services/products-service.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent implements OnInit{

  public categories : CatrgoryModel[]
  public product = new ProductModel()

  public constructor( private peoductsService : ProductsServiceService) {}

  public async ngOnInit(): Promise<void> {
      try{
      this.categories = await this.peoductsService.getAllCategories()
      }catch(err:any){
        console.log(err)
      }
  }


  public async send( ):Promise<void>{
    try{
        this.peoductsService.addProduct(this.product)
        alert("product successfully added!")
    }catch(err:any){
      console.log(err)
    }
  }



}
<p>add-product works!</p>

<!-- <form (ngSubmit)="send()"  ngNativeValidate> -->
    <div class="box flex">
        <form name="addForm" (ngSubmit)="send()" ngNativeValidate>
            <label for="">Product Name:</label>
            <input name="name" type="text" [(ngModel)]="product.name" required>

            <label for="">date & Time:</label>
            <input name="name" type="datetime-local" [(ngModel)]="product.dateTime" required>

            <label for="">Category:</label>
                <select name="select" [(ngModel)]="product.categoryId" required>
                    <option disabled value="">select a category name </option>
                        <option *ngFor="let c of categories" value={{c.categoryId}}>{{c.categoryName}}</option> 
                </select>

                <button>send</button>

        </form>
</div>
