import { Component, Input } from '@angular/core';
import ProductModel from 'src/app/models/ProductModel';
import { ProductsServiceService } from 'src/app/services/products-service.service';

@Component({
  selector: 'app-product-card',
  templateUrl: './product-card.component.html',
  styleUrls: ['./product-card.component.css']
})

export class ProductCardComponent {

  @Input()
  public product : ProductModel

  public constructor( private productsService : ProductsServiceService) {}


  public async deleteItem( prodId : number):Promise <void>{
      try{
        if(!window.confirm("Are you sure?")) return;
        await this.productsService.deleteItem(prodId)
        alert("successfully deleted!")

      }catch(err:any){
        console.log(err)
      }
  }

}
<div class = "card" >
    <img src="https://images.unsplash.com/photo-1656618020911-1c7a937175fd?crop=entropy&cs=tinysrgb&fm=jpg&ixid=MnwzMjM4NDZ8MHwxfHJhbmRvbXx8fHx8fHx8fDE2NTc1MzQyNTE&ixlib=rb-1.2.1&q=80" alt="">
    <div class="card-content">
      <h2>
       {{product.name}}
      </h2>
      <p>
       ID: <strong>{{product.productId}}</strong><br>
        {{product.dateTime}}
      </p>
      <button class="delete-button" (click)="deleteItem(product.productId)">
        Delete Item ❌
        <span class="material-symbols-outlined">
          arrow_right_alt
        </span>
      </button>
    </div>
  </div>
