import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import ProductModel from 'src/app/models/ProductModel';
import { ProductsServiceService } from 'src/app/services/products-service.service';

@Component({
  selector: 'app-add-validation',
  templateUrl: './add-validation.component.html',
  styleUrls: ['./add-validation.component.css']
})
export class AddValidationComponent implements OnInit{
  public product = new ProductModel();

    // @ViewChild("productImage") // Looks for #productImage in the html and assign it to that field:
    // public productImage: ElementRef<HTMLInputElement>;

    public constructor(private productsService: ProductsServiceService, private router: Router) {} // DI

    public async send() {
        try {
            // this.product.image = this.productImage.nativeElement.files[0];
            await this.productsService.addProduct(this.product);
            alert("Product added.");
            this.router.navigateByUrl("/home");
        }
        catch (err: any) {
            alert(err.message);
        }
    }
}
<p>add-validation works!</p>
<div class="box">

    <h2>Add Product</h2>

    <!-- <form (ngSubmit)="send()" ngNativeValidate> -->
    <form (ngSubmit)="send()" #productForm="ngForm">

        <label>Name: </label>
        <input type="text" name="name" [(ngModel)]="product.name" #nameBox="ngModel" required minlength="2">
        <span class="err" *ngIf="nameBox.errors?.['required'] && nameBox.touched">Missing name</span>
        <span class="err" *ngIf="nameBox.errors?.['minlength'] && nameBox.touched">Name must exceed 2 chars</span>

        <label>Price: </label>
        <input type="number" name="price" [(ngModel)]="product.price" #priceBox="ngModel" required min="0" max="1000">
        <span class="err" *ngIf="priceBox.errors?.['required'] && priceBox.touched">Missing price</span>
        <span class="err" *ngIf="priceBox.errors?.['min'] && priceBox.touched">Price can't be negative</span>
        <span class="err" *ngIf="priceBox.errors?.['max'] && priceBox.touched">Price can't exceed 1000</span>

        <label>dateTime: </label>
        <input type="number" name="dateTime" [(ngModel)]="product.dateTime" #dateTime="ngModel" required min="0" max="1000">
        <span class="err" *ngIf="dateTime.errors?.['required'] && dateTime.touched">Missing stock</span>
        <span class="err" *ngIf="dateTime.errors?.['min'] && dateTime.touched">Stock can't be negative</span>
        <span class="err" *ngIf="dateTime.errors?.['max'] && dateTime.touched">Stock can't exceed 1000</span>


        <label>expiration </label>
        <input type="number" name="expirationDateTime" [(ngModel)]="product.expirationDateTime" #expirationDateTime="ngModel" required min="0" max="1000">
        <span class="err" *ngIf="expirationDateTime.errors?.['required'] && expirationDateTime.touched">Missing stock</span>
        <span class="err" *ngIf="expirationDateTime.errors?.['min'] && expirationDateTime.touched">Stock can't be negative</span>
        <span class="err" *ngIf="expirationDateTime.errors?.['max'] && expirationDateTime.touched">Stock can't exceed 1000</span>


        <label for="">Category:</label>
        <select name="categoryId" [(ngModel)]="product.categoryId" #categoryId="ngModel" required min="0" max="1000">
            <option disabled value="">select a category name </option>
                <option *ngFor="let c of categories" value={{c.categoryId}}>{{c.categoryName}}</option> 
        </select>
        <span class="err" *ngIf="categoryId.errors?.['required'] && categoryId.touched">Missing stock</span>
        <span class="err" *ngIf="categoryId.errors?.['min'] && categoryId.touched">Stock can't be negative</span>
        <span class="err" *ngIf="categoryId.errors?.['max'] && categoryId.touched">Stock can't exceed 1000</span>
        <!-- <label>Image: </label>
        <input type="file" accept="image/*" name="image" [(ngModel)]="product.image" #productImage #imageBox="ngModel" required>
        <span class="err" *ngIf="imageBox.errors?.['required'] && imageBox.touched">Missing image</span> -->

        <button [disabled]="productForm.invalid">Add</button>

    </form>

</div>
